import React, { useEffect, useState } from 'react';
import { MDBDataTableV5 } from 'mdbreact';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import { useFormik } from "formik";
import * as Yup from "yup";
import axios from 'axios';

const initialValues = {
    name: "",

    email: "",
    phone:"",
    date: "",
    designation:"",


  };
  const validationSchema = Yup.object({
    name: Yup.string().required("This field is required"),
    email: Yup.string().email().required("Email is required"),
    phone:Yup.number().required("This field is required").min(10,"enter 10 numbers"),
    designation:Yup.string().required("This field is required"),
    date:Yup.date().required('Enter valid date')


  });
export default function SelectSearchTopReverse() {
    const [show, setShow] = useState(false);
    const formik = useFormik({
        initialValues,
        onSubmit,
        validationSchema
      })
      function onSubmit(formData) {
          console.log(formData)
        axios.post("http://localhost:8000/api/v1/contacts/submit-contact", formData).then((response) => {
            alert("Success");
          })  .catch((err) => {
            alert("Error")
          })
      }
    const [edit, setEdit] = useState(false)
  const [datatable, setDatatable] = React.useState({
    columns: [
      {
        label: 'Name',
        field: 'name',
        width: 150,
        attributes: {
          'aria-controls': 'DataTable',
          'aria-label': 'Name',
        },
      },
      {
        label: 'Email',
        field: 'email',
        width: 270,
      },
      {
        label: 'Phone',
        field: 'phone',
        width: 200,
      },

      {
        label: 'Start date',
        field: 'date',
        sort: 'disabled',
        width: 150,
      },
      {
        label: 'Action',
        field: 'action',
        sort: 'disabled',
        width: 100,
      },
    ],
    rows: [
      {
        name: 'Tiger Nixon',
        email: 'System Architect',
        phone: 'Edinburgh',
        age: '61',
        date: '2011/04/25',
        action: '$320',
      },


    ],
  });
  const action = (id) => {
      console.log(id)
      setShow(true)
      setEdit(true)
  }
   useEffect(() => {
       axios.get("http://localhost:8000/api/v1/contacts").then((response) => {
           response.data.map((data) => data.action = (<button onClick={() => action(data._id)}>action</button>))
           setDatatable({...datatable,
        rows:response.data})

       })
   },[])

  return (

   <>
    <Button variant="primary" onClick={() => setShow(true)}>
        Custom Width Modal
      </Button>

      <Modal
        show={show}
        onHide={() => setShow(false)}
        dialogClassName="modal-90w"
        aria-labelledby="example-custom-modal-styling-title"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-custom-modal-styling-title">
            Add new Contact
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
        { edit?null:(<Form  onSubmit={formik.handleSubmit}>
        <Form.Group className="mb-3" >

    <Form.Control type="text" placeholder="Enter name" name="name" {...formik.getFieldProps("name")} />
    {formik.touched.name && formik.errors.name ? (
          <div className="text-danger">{formik.errors.name}</div>
        ) : null}

  </Form.Group>
  <Form.Group className="mb-3" >
    <Form.Control type="email" placeholder="Enter email" name="email" {...formik.getFieldProps("email")} />
    {formik.touched.email && formik.errors.email ? (
          <div className="text-danger">{formik.errors.email}</div>
        ) : null}
  </Form.Group>

  <Form.Group className="mb-3" controlId="formBasicPassword">
    <Form.Control type="number" placeholder="Phone" name="phone" {...formik.getFieldProps("phone")} />
    {formik.touched.phone && formik.errors.phone ? (
          <div className="text-danger">{formik.errors.phone}</div>
        ) : null}
  </Form.Group>
  <Form.Group className="mb-3" >

    <Form.Control type="text" placeholder="designation" name="designation"{...formik.getFieldProps("designation")}   />
    {formik.touched.designation && formik.errors.designation ? (
          <div className="text-danger">{formik.errors.designation}</div>
        ) : null}

  </Form.Group>
  <Form.Group className="mb-3" >

 <Form.Control type="date" placeholder="date" name="date" {...formik.getFieldProps("date")} />
 {formik.touched.date && formik.errors.date ? (
          <div className="text-danger">{formik.errors.date}</div>
        ) : null}
</Form.Group>
  <Button variant="primary" type="submit" >
    Submit
  </Button>
</Form>)}
        </Modal.Body>
      </Modal>
   {/* <button onClick={() => addNewUser()}>Add</button> */}
    <MDBDataTableV5
      hover
      entriesOptions={[5, 20, 25]}
      entries={5}
      pagesAmount={4}
      data={datatable}
      pagingTop
      searchTop
      searchBottom={false}
      barReverse

    />
 </>
  );
}
